import { useEffect, useState } from "react/cjs/react.development"
import { getGifs } from '../helpers/getGifs';

export const useFetchGifs = ( category ) => {

    const [state, setState] = useState({
        data: [],
        loading: true
    });

    useEffect( () => {

        getGifs( category )
            .then( imgs => 
                setTimeout(() => {
                    console.log(imgs)
                    setState({
                        data: imgs,
                        loading: false
                    })

                }, 3000)
        );

    }, [category]);


    return state; 

}
